import { Token } from '@angular/compiler';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { LoginModel } from 'src/app/models/LoginModel';
import { LoginService } from 'src/app/service/login.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent implements OnInit {


  loginForm!: FormGroup;

  constructor(private formBuilder: FormBuilder,
    private router: Router, public loginService: LoginService,) { }

  ngOnInit(): void {
    this.loginForm = this.formBuilder.group(
      {
        email: ['', [Validators.required, Validators.email]],
        senha: ['', [Validators.required]]
      }
    );
  }

  submitLogin() {
    debugger
    var dadosLogin = this.loginForm.getRawValue() as LoginModel;

    // this.loginService.LoginUsuario(dadosLogin)
    //   .subscribe(
    //     token => {
    //       debugger
    //       var nossoToken = token
    //     },
    //     erro => {

    //     })

    this.router.navigate(["/noticias"]);


  }

}
<div class="conteudo-login">

    <form class="conteudo-form" [formGroup]="loginForm" (submit)="submitLogin()">

        <div class="logo">
            Logo
        </div>

        <div>
            <div>
                <input class="input-login" placeholder="email" formControlName="email" />
            </div>
            <div class="mensagem-erro"
                *ngIf="loginForm.get('email')?.errors?.required">
                Campo Obrigatorio!
            </div>
            <div class="mensagem-erro" *ngIf="loginForm.get('email')?.errors?.email">
                formato do Email não e valido!
            </div>
        </div>

        <div>
            <div>
                <input class="input-login" type="password" placeholder="senha" formControlName="senha" />
            </div>
            <div class="mensagem-erro"
                *ngIf="loginForm.get('senha')?.errors?.required">
                Campo Obrigatorio!
            </div>
        </div>

        <div>
            <button type="submit" class="input-submit"> Logar </button>
        </div>

    </form>

</div>
